mapper.select:
SELECT  idx, Cubeuuid, Uid, ContentType, ContentUpdateTime, ContentValue FROM FcubePlayerContent

  AND Cubeuuid = #{cubeuuid} AND Uid = #{uid}
  AND ContentType IN

    #{type}

mapper.update:
update FcubePlayerContent
set
  ContentUpdateTime = #{contentupdatetime,jdbcType=TIMESTAMP},
  ContentValue = #{contentvalue,jdbcType=LONGVARCHAR}
where
ubeuuid = #{cubeuuid,jdbcType=VARCHAR}
AND Uid = #{uid,jdbcType=VARCHAR}
AND ContentType = #{contenttype,jdbcType=VARCHAR}

mapper.delete:
delete from FcubePlayerContent
where
ubeuuid = #{cubeuuid,jdbcType=VARCHAR}
AND Uid = #{uid,jdbcType=VARCHAR}
AND ContentType = #{contenttype,jdbcType=VARCHAR}
